"use client";

import {
  StyleSheet,
  Text,
  View,
  Document,
  Page,
  Image,
  Line,
  Svg,
} from "@react-pdf/renderer";
const styles = StyleSheet.create({
  table: {
    width: "100%",
  },
  row: {
    display: "flex",
    flexDirection: "row",
    borderTop: "1px solid #EEE",
    paddingTop: 8,
    paddingBottom: 8,
  },
  header: {
    borderTop: "none",
  },
  bold: {
    fontWeight: "bold",
  },
  page: {
    padding: 10,
    flexDirection: "row",
    backgroundColor: "#fff",
    display: "flex",
    flexDirection: "column",
  },
  // So Declarative and unDRY ðŸ‘Œ
  row1: {
    width: "27%",
  },
  row2: {
    width: "15%",
  },
  row3: {
    width: "15%",
  },
  row4: {
    width: "20%",
  },
  row5: {
    width: "27%",
  },
  logo: {
    width: "20%",
    height: "20%",
    backgroundColor: "red",
  },
  image: {
    width: 10,
    height: 10,
  },

  nameHead: {
    fontWeight: "500",
    fontSize: "25",
    textAlign: "center",
    width: "100%",
    textTransform: "uppercase",
  },
  profation: {
    fontWeight: "thin",
    fontSize: "15",
    textAlign: "center",
    width: "100%",
    padding: 5,
    textTransform: "uppercase",
  },
  line: {
    width: "100%",
    height: "10",
    marginTop: 5,
    padding: 0,
  },
  view1: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    width: "100%",
    margin: "auto",
  },
  viewProfile:{
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    width: "75%",
    margin: "auto",
  },
  view2: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    width: "50%",
    gap: 5,
    padding: 5,
  },
  view3: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "flex-start",
    width: "100%",
    fontSize: 10,
  },
  view4: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    width: "100%",
    fontSize: 10,
  },
  text: {
    fontWeight: "black",
  },
  viewcommon: {
    display: "flex",
    padding: 8,
    flexDirection: "row",
    alignItems: "flex-start",
  },
  image2: {
    width: 20,
    height: 20,
  },
});
const telephon =
  "data:image/png;base64,";
const email =
  "data:image/png;base64,";
const location =
  "data:image/jpeg;base64,";
const bag =
  "data:image/png;base64,";
const people =
  "data:image/png;base64,";
const education =
  "data:image/png;base64,";
const flag =
  "data:image/png;base64,";
export const ReactPdf = ({ data }) => {
  return data ? (
    <Document>
      <Page size='A4' style={styles.page}>
        <View style={styles.table}>
          {/* header  */}
          {data["profile"] ? (
            <>
              <Text style={styles.nameHead}>{data["profile"]["name"]}</Text>
              <Text style={styles.profation}>
                {data["profile"]["profession"]}
              </Text>
              <Svg style={styles.line}>
                <Line
                  x1='0'
                  y1='0.3'
                  x2='1000'
                  y2='0.3'
                  strokeWidth={2}
                  stroke='rgb(0,0,0)'
                />
              </Svg>
            </>
          ) : null}
          {/* self information */}
          {data["profile"] ? (
            <>
              <View style={styles.viewProfile}>
                <View style={styles.view2}>
                  <View style={styles.view3}>
                    <Image style={styles.image} src={telephon} />
                    <Text>:{data["profile"]["phone"]}</Text>
                  </View>
                  <View style={styles.view3}>
                    <Image style={styles.image} src={email} />
                    <Text>:{data["profile"]["email"]}</Text>
                  </View>
                  <View style={styles.view3}>
                    <Image style={styles.image} src={location} />
                    <Text>:{data["profile"]["address"]}</Text>
                  </View>
                </View>
                <View style={styles.view2}>
                  <View style={styles.view4}>
                    <Text style={{ fontWeight: "extrabold", color: "#000" }}>
                      Date Of Birth:
                    </Text>
                    <Text>{data["profile"]["birth"]}</Text>
                  </View>
                  <View style={styles.view4}>
                    <Text style={{ fontWeight: "extrabold", color: "#000" }}>
                      Gender:
                    </Text>
                    <Text>{data["profile"]["gender"]}</Text>
                  </View>
                  <View style={styles.view4}>
                    <Text style={{ fontWeight: "extrabold", color: "#000" }}>
                      Marital Status:
                    </Text>
                    <Text>{data["profile"]["married"]}</Text>
                  </View>
                  <View style={styles.view4}>
                    <Text style={{ fontWeight: "extrabold", color: "#000" }}>
                      Nationality:
                    </Text>
                    <Text>{data["profile"]["nationality"]}</Text>
                  </View>
                </View>
              </View>
              <Svg style={styles.line}>
                <Line
                  x1='0'
                  y1='0.3'
                  x2='1000'
                  y2='0.3'
                  strokeWidth={2}
                  stroke='rgb(0,0,0)'
                />
              </Svg>
            </>
          ) : null}

          {/* summary */}
          {data["summary"] ? (
            <View style={styles.view1}>
              <Text
                style={{
                  paddingTop: 5,
                  paddingLeft: 15,
                  fontSize: 10,
                  lineHeight: 1.5,
                }}>
                {data["summary"] ? data["summary"]["text"] : ""}
              </Text>
            </View>
          ) : null}
          {/* work exprience */}
          {data["experience"] &&
          data["experience"] instanceof Array &&
          data["experience"].length > 0 ? (
            <>
              <View style={styles.view1}>
                <View style={styles.viewcommon}>
                  <Image style={styles.image2} src={bag} />
                  <Text style={styles.text}>Work Experience</Text>
                </View>
              </View>
              <View
                style={{ display: "flex", flexDirection: "column", gap: 5 }}>
                {data["experience"] instanceof Array &&
                  data["experience"].length > 0 &&
                  data["experience"].map((item, i) => {
                    return (
                      <View
                        key={item["id"]}
                        style={{
                          display: "flex",
                          flexDirection: "row",
                          gap: 10,
                          alignItems: "flex-start",
                        }}>
                        <View
                          style={{
                            display: "flex",
                            alignItems: "flex-start",
                            padding: 10,
                          }}>
                          <Text style={{ fontSize: 10 }}>{item["start"]}-</Text>
                          <Text style={{ fontSize: 10 }}>{item["end"]}</Text>
                        </View>
                        <View style={{ width: "100%" }}>
                          <View
                            style={{ display: "flex", gap: 4, width: "100%" }}>
                            <Text style={{  fontWeight: "bold",fontSize:14  }}>
                              {item["title"]}
                            </Text>
                            <View
                              style={{
                                width: "100%",
                                display: "flex",
                                flexDirection: "row",
                              }}>
                              <Text style={{ fontSize: 10, width: "50%" }}>
                                {item["city"]}
                              </Text>
                              <Text
                                style={{
                                  fontSize: 10,
                                  fontWeight: "black",
                                  width: "50%",
                                }}>
                                {item["state"]}
                              </Text>
                            </View>
                          </View>
                          <View
                            style={{
                              padding: 10,
                              width: "90%",
                              display: "flex",
                              gap: 5,
                            }}>
                            <Text style={{ fontSize: 8, lineHeight: 1.5 }}>
                              .{item['employer']}
                            </Text>
                          </View>
                          <Svg style={styles.line}>
                            <Line
                              x1='0'
                              y1='0.1'
                              x2='1000'
                              y2='0.1'
                              strokeWidth={2}
                              stroke='rgb(0,0,0)'
                            />
                          </Svg>
                        </View>
                      </View>
                    );
                  })}
              </View>
            </>
          ) : null}
          {/* education */}
          {data["education"] &&
          data["education"] instanceof Array &&
          data["education"].length > 0 ? (
            <>
              <View style={styles.view1}>
                <View style={styles.viewcommon}>
                  <Image style={styles.image2} src={education} />
                  <Text style={styles.text}>Education</Text>
                </View>
              </View>
              <View>
                <View
                  style={{ display: "flex", flexDirection: "column", gap: 5 }}>
                  {data["education"].map(item => {
                    return (
                      <View
                        key={item["id"]}
                        style={{
                          display: "flex",
                          flexDirection: "row",
                          gap: 5,
                        }}>
                        <View
                          style={{
                            display: "flex",
                            alignItems: "flex-start",
                          
                            padding: 10,
                          }}>
                          <Text style={{ fontSize: 10 }}>{item["start"]}-</Text>
                          <Text style={{ fontSize: 10 }}>{item["end"]}</Text>
                        </View>
                        <View style={{ width: "100%" }}>
                          <View
                            style={{ display: "flex", gap: 4, width: "100%" }}>
                            <Text style={{  fontWeight: "bold",fontSize:14  }}>
                              {item["school"]}
                            </Text>
                            <View
                              style={{
                                width: "100%",
                                display: "flex",
                                flexDirection: "row",
                              }}>
                              <Text style={{ fontSize: 10, width: "50%" }}>
                                {item["studyfield"]}
                              </Text>
                              <Text
                                style={{
                                  fontSize: 10,
                                  fontWeight: "black",
                                  width: "50%",
                                }}>
                                {item["city"]}
                              </Text>
                            </View>
                          </View>
                          <Svg style={styles.line}>
                          <Line
                            x1='0'
                            y1='0.001'
                            x2='1000'
                            y2='0.001'
                            strokeWidth={2}
                            stroke='rgb(0,0,0)'
                          />
                        </Svg>
                        </View>
                       
                      </View>
                    );
                  })}
                </View>
              </View>
            </>
          ) : null}
          {/* hobbies */}
          {data["hobbies"] &&
          data["hobbies"] instanceof Array &&
          data["hobbies"].length > 0 ? (
            <View style={{ display: "flex", flexDirection: "column", gap: 5 }}>
              <View style={styles.view1}>
                <View style={styles.viewcommon}>
                  <Image style={styles.image2} src={flag} />
                  <Text style={styles.text}>Hobbies</Text>
                </View>
              </View>
              <View
                style={{
                  width: "100%",
                  display: "flex",
                  gap: 5,
                  justifyContent: "center",
                  flexDirection: "row",
                  flexWrap: "wrap",
                }}>
                <View
                  style={{
                    width: "70%",
                    display: "flex",
                    flexDirection: "row",
                    gap: "5",
                    justifyContent: "flex-start",
                    alignItems: "flex-start",
                  }}>
                  {data["hobbies"].map(item => {
                    return (
                      <Text
                        key={item["id"]}
                        style={{
                          backgroundColor: "gray",
                          borderRadius: 5,
                          textAlign: "center",
                          fontSize: 12,
                          padding: 5,
                          width: "50%",
                        }}>
                        {item["name"]}
                      </Text>
                    );
                  })}
                </View>
              </View>
            </View>
          ) : null}
          {/* reference */}
          {data["reference"] instanceof Array &&
          data["reference"].length > 0 ? (
            <View style={{ display: "flex", flexDirection: "column", gap: 5 }}>
              <View style={styles.view1}>
                <View style={styles.viewcommon}>
                  <Image style={styles.image2} src={people} />
                  <Text style={styles.text}>Reference</Text>
                </View>
              </View>
              <View
                style={{
                  width: "75%",
                  display: "flex",
                  justifyContent: "center",
                  flexDirection: "row",
                  flexWrap: "wrap",
                  margin:"auto"
                }}>
                {data["reference"] &&
                  data["reference"].map(item => {
                    return (
                      <View
                        key={item["id"]}
                        style={{
                          width: "45%",
                          display: "flex",
                          gap: 5,
                          justifyContent:"flex-start"
                        }}>
                        <Text style={{ fontWeight: "bold",fontSize:14 }}>
                          {item["name"]}
                        </Text>
                        <Text style={{ fontSize: 10 }}>
                          {item["companyName"]}
                        </Text>
                        <Text style={{ fontSize: 10 }}>{item["mobile"]}</Text>
                        <Text style={{ fontSize: 10 }}>{item["email"]}</Text>
                        <Text style={{ fontSize: 10 }}>{item["position"]}</Text>
                      </View>
                    );
                  })}
              </View>
            </View>
          ) : null}
        </View>
      </Page>
    </Document>
  ) : null;
};
export default ReactPdf;
